mapper.select:
SELECT p.post_id, p.user_id, p.title, p.content, p.views, p.board_cat, DATE_FORMAT(p.write_date, '%m-%d %H:%i') AS write_date, COUNT(l.user_id) AS likes FROM posts p LEFT JOIN post_like l ON p.post_id = l.post_id WHERE p.board_cat=#{board_cat}
AND ${searchKey} LIKE '%${searchWord}%'
GROUP BY p.post_id order by post_id desc limit ${onePageRecord} offset ${offsetPoint}

mapper.update:
update posts
set views = views + 1
where post_id = ${param1};
