mapper.select:
SELECT COUNT(*)
FROM chat_list
WHERE from_id = #{userId}
   OR to_id = #{userId}

select *
from chat_list
where from_id = #{userId}
   or to_id = #{userId}
ORDER BY last_time DESC
    LIMIT #{pvo.offsetPoint}, #{pvo.onePageRecord}

SELECT COUNT(*)
FROM message
WHERE chat_id = #{chatId}
  AND is_read = 0
  AND is_from_id != #{isFromId}

SELECT profile_img
FROM users
WHERE user_id = #{userId}
