mapper.select:
select count(user_id)
from users
where user_id = #{param1};

select password, salt, user_name
from users
where user_id = #{param1};

select user_id
from users
where user_name = #{param1}
  and email = #{param2};

select count(user_id)
from users
where user_id = #{param1}
  and email = #{param2};

mapper.update:
update users
set password = #{userpwd},
    salt     = #{salt}
where user_id = #{userid};
